INSERT INTO `pz_douban_movie` VALUES (1775569, '青岛要塞爆击命令', ' 青岛要塞大爆炸', 0.00, 0, 'https://movie.douban.com/subject/1775569/', 1, '古泽宪吾/圆谷英二', '须崎胜弥', '加山雄三/佐藤允/夏木阳介', '战争', '1963-05-29(日本)', '', 1543332345, 1543332345, '日本映画としては珍しく、第一次世界大戦を扱った作品。青島の戦いを題材に、日本初の海軍航空隊としてドイツ軍に立ち向かった若者たちを描く。', '', ' 日本');
