INSERT INTO `pz_douban_movie` VALUES (4197788, '强盗', ' 盗徒末路 / 马拉松大盗 / 抢匪(台) / The Robber', 6.70, 1133, 'https://movie.douban.com/subject/4197788/', 1, '本杰明·海森堡', '本杰明·海森堡/Martin Prinz', 'Andreas Lust/Franziska Weisz/Florian Wotruba', '剧情/传记/犯罪/运动', '2010-03-04', '', 1543332417, 1543332417, '雷敦伯格（安德烈·鲁斯特 Andreas Lust 饰）和女友艾丽卡（Franziska Weiss 饰）生活在风景如画的维也纳，表面上看来，他们和世间的大多数情侣一样恩爱缠绵，可实际上，雷敦伯格有着一段不为人知的过去。', '', ' 德国 / 奥地利');
